import{_ as e,c as a,o as d,ag as n}from"./chunks/framework.CQuhCYrb.js";const u=JSON.parse('{"title":"AgentKit","description":"","frontmatter":{},"headers":[],"relativePath":"content/1.introduction/1.overview.md","filePath":"content/1.introduction/1.overview.md","lastUpdated":1764227036000}'),i={name:"content/1.introduction/1.overview.md"};function o(r,t,l,g,c,h){return d(),a("div",null,[...t[0]||(t[0]=[n('<h1 id="agentkit" tabindex="-1">AgentKit <a class="header-anchor" href="#agentkit" aria-label="Permalink to &quot;AgentKit&quot;">​</a></h1><h2 id="什么是-agentkit" tabindex="-1">什么是 AgentKit？ <a class="header-anchor" href="#什么是-agentkit" aria-label="Permalink to &quot;什么是 AgentKit？&quot;">​</a></h2><p>AgentKit是火山引擎为AI Agent构建、部署、运行提供支持的开发工具平台，帮助开发者和企业降低 Agent 开发门槛，为Agent 提供模型之外的安全、内置工具、记忆、知识、监控、评测等基础设施，助力企业高效构建、部署、运维复杂智能的企业级Agent。平台也包含一个Python SDK和Starter Toolkit，来帮助开发者通过SDK/CLI来构建、部署、发布和管理Agent应用。</p><h2 id="agentkit-platform" tabindex="-1">AgentKit Platform <a class="header-anchor" href="#agentkit-platform" aria-label="Permalink to &quot;AgentKit Platform&quot;">​</a></h2><p>AgentKit Platform 为 Agent 应用提供：</p><ul><li><strong>Runtime 环境</strong>：提供隔离的运行环境，支持容器化部署</li><li><strong>基础设施服务</strong>： <ul><li>Memory（记忆管理）</li><li>Knowledge（知识库）</li><li>Identity（身份认证）</li><li>Gateway（MCP 工具聚合与路由）</li></ul></li></ul><p>通过 AgentKit Platform，开发者无需关心底层基础设施，可以专注于 Agent 业务逻辑的开发。</p><h2 id="agentkit-sdk" tabindex="-1">AgentKit SDK <a class="header-anchor" href="#agentkit-sdk" aria-label="Permalink to &quot;AgentKit SDK&quot;">​</a></h2><p>AgentKit SDK 是一个Python开发工具包，内置了对AgentKit Platform的所有服务的客户端，开发者可以通过SDK来调用Platform的所有服务。</p><h2 id="agentkit-cli" tabindex="-1">AgentKit CLI <a class="header-anchor" href="#agentkit-cli" aria-label="Permalink to &quot;AgentKit CLI&quot;">​</a></h2><p>AgentKit CLI 是一个命令行工具，用于简化 Agent 应用的全生命周期管理，包括初始化、配置、构建、部署和运维。</p><h3 id="主要命令" tabindex="-1">主要命令 <a class="header-anchor" href="#主要命令" aria-label="Permalink to &quot;主要命令&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>功能</th><th>说明</th></tr></thead><tbody><tr><td><code>agentkit init</code></td><td>初始化项目</td><td>创建 Agent 应用模板，支持多种框架</td></tr><tr><td><code>agentkit config</code></td><td>配置项目</td><td>交互式配置部署和运行参数</td></tr><tr><td><code>agentkit build</code></td><td>构建镜像</td><td>构建符合标准的 Docker 镜像</td></tr><tr><td><code>agentkit deploy</code></td><td>部署应用</td><td>将镜像部署到 Platform Runtime</td></tr><tr><td><code>agentkit launch</code></td><td>一键部署</td><td>build + deploy 的组合命令</td></tr><tr><td><code>agentkit invoke</code></td><td>调用应用</td><td>向已部署的 Agent 发送请求</td></tr><tr><td><code>agentkit status</code></td><td>查看状态</td><td>查看 Runtime 运行状态</td></tr><tr><td><code>agentkit destroy</code></td><td>销毁资源</td><td>释放 Runtime 实例和相关资源</td></tr></tbody></table>',13)])])}const k=e(i,[["render",o]]);export{u as __pageData,k as default};
